mapper.select:
SELECT id, name, label, type, created_date, updated_date, used_name
FROM actor
WHERE id = #{id}

SELECT id, name, label, type, created_date, updated_date, used_name
FROM actor
WHERE label = #{label}


SELECT id, name, label, type, created_date, updated_date, used_name
FROM actor
WHERE name like #{patten}

mapper.update:
UPDATE actor
SET name= #{name}, label = #{label}, type = #{type}, created_date = #{createdDate}, updated_date = #{updatedDate}, used_name = #{usedName}
WHERE id = #{id}
